import React from 'react';
import axios from 'axios';
import ReactDOM from 'react-dom/client';
import reportWebVitals from './reportWebVitals';


function MediaPlayTab(props:any) {
		return ( 
		<div>
			<h1>Hi, you want to download some mediaspace content?</h1>
			<p>Come on in</p>
			<input type="file" onChange={props.onFileChange} hidden id="mediaInput"/>
			<label htmlFor="mediaInput"> 
				{props.FileValue !== "" ? props.FileValue.name : "Select a file"}
			</label>
			<input type="text" onChange={props.onVideoLinkProvided} value={props.videoLinkValue} id="mediaVideoLink"/>
			<input type="text" onChange={props.onPlaylistLinkProvided} value={props.playlistLinkValue} id="mediaPlaylistLink"/>
			<button onClick={props.onFileUpload}>
				Upload!
			</button>
		</div>
		);
	}
function EchoTab(props:any) {
	return (
	<div>
		<h1> Hi, you want to spin around in echo360?</h1>
		<p> Take a number pal</p>
		<input type="file" onChange={props.onFileChange} hidden id="echoInput"/>
		<label htmlFor="echoInput">
			{props.FileValue !== "" ? props.FileValue.name : "Select a file"}
		</label>
		<input type="text" onChange={props.onVideoLinkProvided} value={props.videoLinkValue} id="echoVideoLink"/>
		<input type="text" onChange={props.onPlaylistLinkProvided} value={props.playlistLinkValue} id="echoPlaylistLink"/>
		<button onClick={props.onFileUpload}>
			Upload!
		</button>
	</div>
	);
}

function DownloadWait(props:any){
	return ( 
		<h2> Downloading your {props.downloadObject}</h2>
	);
}

function DownloadReset(props:any){
	return ( 
		<h2>Your download has been completed. Hit the button below to download another video/playlist</h2>
	);
}

function DownloadSelection(props:any) {

	if(props.toggledMediaSpace === true) {
		return <MediaPlayTab {...props}/> ; 
	}
	else {
		return <EchoTab {...props}/> ; 
	}
}


class MyApp extends React.Component<{},{toggledMediaSpace:boolean, selectedFile:any,videoLinkString:string,playlistLinkString:string,downloading:boolean}> {

	constructor(props:any) {
		super(props)

		this.state = {toggledMediaSpace:true,selectedFile:"",videoLinkString:"",playlistLinkString:"",downloading:false};

		this.handleMediaSpaceButton = this.handleMediaSpaceButton.bind(this);
		this.handleEchoButton = this.handleEchoButton.bind(this);
		this.onFileChange = this.onFileChange.bind(this);
		this.onFileUpload = this.onFileUpload.bind(this);
		this.onVideoLinkProvided = this.onVideoLinkProvided.bind(this);
		this.onPlaylistLinkProvided = this.onPlaylistLinkProvided.bind(this);
	}

	onFileChange(e:any) {
		this.setState({selectedFile: e.target.files[0]});
	}

	onFileUpload() {

		if (this.state.videoLinkString !== "" && this.state.playlistLinkString === "") {
			console.log("File succesfully uploaded with filename: " + this.state.selectedFile.name);
			let formData = new FormData() ; 
			formData.append("cookieFile",this.state.selectedFile);
			formData.append("videoLinkString",this.state.videoLinkString);

			this.setState({downloading:true});
			axios.post('http://192.168.1.216:5000/video', formData, { 
				headers: {
					'Content-Type': 'multipart/form-data'
				}
			}).then(
				res => {
				console.log(res);
			}).catch( 
				error => {
				console.log(error); 
			});
		}

		else if  (this.state.playlistLinkString !== "" && this.state.videoLinkString === "") {
			console.log("File succesfully uploaded with filename: " + this.state.selectedFile.name);
			let formData = new FormData() ; 
			formData.append("cookieFile",this.state.selectedFile);
			formData.append("playlistLinkString",this.state.playlistLinkString);

			this.setState({downloading:true});
			axios.post('http://192.168.1.216:5000/playlist', formData, { 
				headers: {
					'Content-Type': 'multipart/form-data'
				}
			}).then(
				res => {
				console.log(res);
			}).catch( 
				error => {
				console.log(error); 
			});
		}

		else {
			alert("Please fill only one of the video/playlist link fields");
		}
	}
	onVideoLinkProvided(e:any) {
		this.setState({videoLinkString:e.target.value})
	}

	onPlaylistLinkProvided(e:any) {
		this.setState({playlistLinkString:e.target.value})

	}

	onDownloadingContent(){

	}
	handleMediaSpaceButton() {
		this.setState({
			toggledMediaSpace:true
		});
	}

	handleEchoButton() {
		this.setState({
			toggledMediaSpace:false
		});
	}

	render() {
		return ( 
			<div>
				<button onClick={this.handleMediaSpaceButton}>
					MediaPlay 
				</button>
				<button onClick={this.handleEchoButton}>
					Echo360 
				</button>
				<div>
					<DownloadSelection toggledMediaSpace={this.state.toggledMediaSpace} onFileChange={this.onFileChange} onFileUpload={this.onFileUpload} FileValue={this.state.selectedFile} onVideoLinkProvided={this.onVideoLinkProvided} videoLinkValue={this.state.videoLinkString} onPlaylistLinkProvided={this.onPlaylistLinkProvided} playlistLinkValue={this.state.playlistLinkString} />
				</div>
			</div>
		);
	}
}

const root = ReactDOM.createRoot(
  document.getElementById('root') as HTMLElement
);
root.render(<MyApp />);

// If you want to start measuring performance in your app, pass a function
// to log results (for example: reportWebVitals(console.log))
// or send to an analytics endpoint. Learn more: https://bit.ly/CRA-vitals
reportWebVitals();
