function MediaPlayTab(props:any) {
		return ( 
		<div className={props.className}>
			<h1>Hi, you want to download some mediaspace content?</h1>
			<p>Come on in</p>
			<input type="file" onChange={props.onFileChange} hidden id="mediaInput"/>
			<label htmlFor="mediaInput"> 
				{props.selectedFile !== "" ? props.selectedFile.name : "Select a file"}
			</label>
			<label htmlFor="mediaVideoLink"> Input a video link here </label> 
			<input type="text" onChange={props.onVideoLinkProvided} value={props.videoLinkString} id="mediaVideoLink"/>
			<label htmlFor="mediaPlaylistLink"> Input a playlist link here </label> 
			<input type="text" onChange={props.onPlaylistLinkProvided} value={props.playlistLinkValue} id="mediaPlaylistLink"/>
			<button onClick={props.onFileUpload}>
				Upload!
			</button>
		</div>
		);
}


export default MediaPlayTab;
