import React from 'react';
import axios from 'axios';
import ReactDOM from 'react-dom/client';
import reportWebVitals from './reportWebVitals';


function MediaPlayTab(props:any) {
		return ( 
		<div>
			<h1>Hi, you want to download some mediaspace content?</h1>
			<p>Come on in</p>
			<input type="file" onChange={props.onFileChange} hidden id="mediaInput"/>
			<label htmlFor="mediaInput"> {props.FileValue !== "" ? props.FileValue.name : "Select a file"} </label>
			<button onClick={props.onFileUpload}>
				Upload!
			</button>
		</div>
		);
	}
function EchoTab(props:any) {
	return (
	<div>
		<h1> Hi, you want to spin around in echo360?</h1>
		<p> Take a number pal</p>
		<input type="file" onChange={props.onFileChange} hidden id="echoInput"/>
		<label htmlFor="echoInput"> {props.FileValue !== "" ? props.FileValue.name : "Select a file"} </label>
		<button onClick={props.onFileChange}>
			Upload!
		</button>
	</div>
	);
}

function DownloadWindow(props:any) {

	if(props.toggledMediaSpace === true) {
		return <MediaPlayTab onFileChange={props.onFileChange} onFileUpload={props.onFileUpload} FileValue={props.FileValue} /> ; 
	}
	else {
		return <EchoTab onFileChange={props.onFileChange} onFileUpload={props.onFileUpload} FileValue={props.FileValue} /> ; 
	}
}


class MyApp extends React.Component<{},{toggledMediaSpace:boolean, selectedFile:any,linkString:string}> {

	constructor(props:any) {
		super(props)

		this.state = {toggledMediaSpace:true,selectedFile:"",linkString:""};

		this.handleMediaSpaceButton = this.handleMediaSpaceButton.bind(this);
		this.handleEchoButton = this.handleEchoButton.bind(this);
		this.onFileChange = this.onFileChange.bind(this);
		this.onFileUpload = this.onFileUpload.bind(this);
	}

	onFileChange(e:any) {
		this.setState({selectedFile: e.target.files[0]});
	}

	onFileUpload() {
		console.log("File succesfully uploaded with filename: " + this.state.selectedFile.name);
		axios.get('http://localhost:5000').then(
			res => {
				const data = res.data;
				console.log(data);
			}
		)
	}
	onLinkProvided(e:any) {
		this.setState({linkString:e.target.value})
	}
	handleMediaSpaceButton() {
		this.setState({
			toggledMediaSpace:true
		});
	}

	handleEchoButton() {
		this.setState({
			toggledMediaSpace:false
		});
	}

	render() {
		return ( 
			<div>
				<button onClick={this.handleMediaSpaceButton}>
					MediaPlay 
				</button>
				<button onClick={this.handleEchoButton}>
					Echo 
				</button>
				<div>
					<DownloadWindow toggledMediaSpace={this.state.toggledMediaSpace} onFileChange={this.onFileChange} onFileUpload={this.onFileUpload} FileValue={this.state.selectedFile}/>
				</div>
			</div>
		);
	}
}

const root = ReactDOM.createRoot(
  document.getElementById('root') as HTMLElement
);
root.render(<MyApp />);

// If you want to start measuring performance in your app, pass a function
// to log results (for example: reportWebVitals(console.log))
// or send to an analytics endpoint. Learn more: https://bit.ly/CRA-vitals
reportWebVitals();
